// UploadProfilePictureModal.js

import React, { useState } from 'react';

const UploadProfilePictureModal = ({ isOpen, onClose, onUpload }) => {
  const [selectedImage, setSelectedImage] = useState(null);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setSelectedImage(file);
  };

  const handleUpload = async () => {
    try {
      const formData = new FormData();
      formData.append('image', selectedImage); // 'image' should match your server's file upload field name

      // Send the image to the server
      const response = await fetch('/api/upload', {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        const newImageUrl = await response.json(); // Assuming the server responds with the new image URL
        onUpload(newImageUrl);
        onClose();
      } else {
        console.error('Image upload failed.');
      }
    } catch (error) {
      console.error('Error uploading image:', error);
    }
  };

  // Log for debugging
  console.log('isOpen:', isOpen);

  return (
    <div className={`modal ${isOpen ? 'open' : ''}`}>
      <div className="modal-content">
        <h3>Upload Profile Picture</h3>
        <input type="file" accept="image/*" onChange={handleImageChange} />
        <button onClick={handleUpload}>Upload</button>
        <button onClick={onClose}>Cancel</button>
      </div>
    </div>
  );
};

export default UploadProfilePictureModal;
