import React from 'react';
import { Doughnut } from 'react-chartjs-2'; // Import Doughnut from react-chartjs-2
import styled from 'styled-components';
import { useGlobalContext } from '../../context/globalContext';
import { dateFormat } from '../../utils/dateFormat';

function Chart() {
  const { incomes, expenses } = useGlobalContext();

  // Modify data for a Doughnut chart
  const data = {
    labels: ['Income', 'Expenses'],
    datasets: [
      {
        data: [
          ...Chart.incomes.reduce((total, income) => total + income.amount, 0),
          expenses.reduce((total, expense) => total + expense.amount, 0),
        ],
        backgroundColor: ['green', 'red'],
      },
    ],
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false, // Prevent the chart from maintaining an aspect ratio
  };

  return (
    <ChartStyled>
      <Doughnut data={data} options={options} /> {/* Render a Doughnut chart */}
    </ChartStyled>
  );
}

const ChartStyled = styled.div`
  background: #fcf6f9;
  border: 2px solid #ffffff;
  box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
  padding: 1rem;
  border-radius: 20px;
  height: 100%;
`;

export default Chart;
