// ProfilePicture.js

import React, { useState } from 'react';
import { useAuthContext } from '../hooks/useAuthContext';
import UploadProfilePictureModal from '../UploadProfilePictureModal'; // Import the modal component

const ProfilePicture = () => {
  const { user } = useAuthContext();
  const [isUploadModalOpen, setIsUploadModalOpen] = useState(false);

  const handleUploadClick = () => {
    setIsUploadModalOpen(true);
  };

  const handleUploadComplete = (newImageUrl) => {
    // Handle updating the user's profile picture with the newImageUrl
    // This logic will depend on how you handle user data and images
  };

  return (
    <div className="profile-picture">
      <img src={user.profilePicture} alt="Profile" />
      <button onClick={handleUploadClick}>Change Picture</button>
      <UploadProfilePictureModal
        isOpen={isUploadModalOpen}
        onClose={() => setIsUploadModalOpen(false)}
        onUpload={handleUploadComplete}
      />
    </div>
  );
};

export default ProfilePicture;
