import React from 'react'
import {Chart as ChartJs, 
    DoughnutController,
    ArcElement,
    Title,
    Tooltip,
    CategoryScale,
    LinearScale,
  } from 'chart.js';

import {Doughnut} from 'react-chartjs-2'
import styled from 'styled-components'
import { useGlobalContext } from '../../context/globalContext'
import { dateFormat } from '../../utils/dateFormat'


ChartJs.register(
    DoughnutController,
    ArcElement,
    Title,
    Tooltip,
    CategoryScale,
    LinearScale,
  );

function Chart() {
    const {incomes, expenses} = useGlobalContext()

    const incomeColor = 'green';
    // Assign unique colors to each expense based on its index
  const expenseColors = expenses.map((_, index) => getRandomColor());



    const data = {
        labels: ['Income', 'Expenses'],
        datasets: [
          {
            data: [
              incomes.reduce((total, income) => total + income.amount, 0),
              expenses.reduce((total, expense) => total + expense.amount, 0),
            ],
            backgroundColor: [incomeColor, expenseColors],
          },
        ],
      };

      return (
        <ChartStyled>
          <Doughnut data={data} />
        </ChartStyled>
      );
}

const ChartStyled = styled.div`
    background: #FCF6F9;
    border: 2px solid #FFFFFF;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
    padding: 1rem;
    border-radius: 20px;
    height: 100%;
`;

export default Chart

// Implement a function to generate random colors
function getRandomColor() {
    const letters = '0123456789ABCDEF';
    let color = '#';
    for (let i = 0; i < 6; i++) {
      color += letters[Math.floor(Math.random() * 16)];
    }
    return color;
  }
